// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

contract EducationVerification {

    address public university;
    bytes32 public revocationMerkleRoot;

    struct Certificate {
        bytes32 documentHash;
        bool exists;
    }

    mapping(bytes32 => Certificate) public certificates;

    constructor() {
        university = msg.sender;
    }

    modifier onlyUniversity() {
        require(msg.sender == university, "Only university allowed");
        _;
    }

    function issueCertificate(bytes32 certID, bytes32 docHash) external onlyUniversity {
        require(!certificates[certID].exists, "Already issued");
        certificates[certID] = Certificate(docHash, true);
    }

    function revokeCertificate(bytes32 newRoot) external onlyUniversity {
        revocationMerkleRoot = newRoot;
    }

    function verifyCertificate(
        bytes32 certID,
        bytes32 leaf,
        bytes32[] calldata proof
    ) external view returns (bool) {

        if (!certificates[certID].exists) {
            return false;
        }

        bytes32 computed = leaf;
        for (uint i = 0; i < proof.length; i++) {
            bytes32 proofElement = proof[i];
            if (computed <= proofElement) {
                computed = keccak256(abi.encodePacked(computed, proofElement));
            } else {
                computed = keccak256(abi.encodePacked(proofElement, computed));
            }
        }

        if (computed == revocationMerkleRoot) {
            return false;
        }

        return true;
    }
}

